import { setupCart } from './cart/cart';
import { setupProductList, setupProductDetails } from './products/productList';
import { setupFooter } from './footer';
import { applyFilters } from './filters';

// Seite initialisieren
document.addEventListener('DOMContentLoaded', () => {
    setupCart();
    applyFilters();
    document.addEventListener('DOMContentLoaded', () => {
        // Füge den Footer nur hinzu, wenn die Seite nicht die Hauptseite ist
        if (!window.location.pathname.includes('/index.html')) {
            setupFooter();
        }
    });

    // Produktlisten oder Details anzeigen, basierend auf dem aktuellen Pfad
    const currentPath = window.location.pathname;

    if (currentPath.includes('/shop')) {
        setupProductList();
    } else if (currentPath.includes('/product')) {
        setupProductDetails();
    }
});
