import { fetchProducts } from '../utils/helpers';

export async function setupProductList() {
    const products = await fetchProducts();
    const productContainer = document.getElementById('product-container');

    products.forEach(product => {
        const productCard = document.createElement('div');
        productCard.className = 'product-card';
        productCard.innerHTML = `
            <a href="/product/${product.id}" class="product-link">
                <img src="${product.images[0]}" alt="${product.name}">
                <h2>${product.name}</h2>
            </a>
            <p class="product-price">${product.price} €</p>
        `;

        productContainer.appendChild(productCard);
    });
}

export function setupProductDetails() {
    const pathParts = window.location.pathname.split('/');
    const productId = pathParts[pathParts.length - 1];

    fetchProducts().then(products => {
        const product = products.find(p => p.id === productId);
        if (!product) {
            document.getElementById('product-detail-container').innerHTML = `<p>Product not found.</p>`;
            return;
        }

        // Produktinformationen anzeigen
        document.querySelector('.product-info').innerHTML = `
            <h1>${product.name}</h1>
            <p>${product.description}</p>
            <p>${product.price} €</p>
        `;
    });
}
