import { createFooter } from './footer.js';
import { updateCartCount } from './cart/cart.js';
import { displayProductList, displayProductDetails } from './products/products.js';

document.addEventListener('DOMContentLoaded', () => {
    updateCartCount();

    if (!document.getElementById('landing-container')) {
        createFooter();
    }

    const currentPath = window.location.pathname;

    if (document.getElementById('product-container')) {
        const category = currentPath.includes('/bags') ? 'bags' : 'all';
        displayProductList(category);
    } else if (document.getElementById('product-detail-container')) {
        displayProductDetails();
    }
});
